mapper.delete:
    	DELETE FROM  TAB_ROLE_OPERATE where 1=1 

	    	 and ROLE_UUID in

					  #{item}

mapper.insert:
    		insert into TAB_ROLE_OPERATE 
			select a.ROLE_ID,b.UUID from tab_role_resource a,tab_operate b where a.RESOURCE_ID = b.RESOURCE_UUID 

					and a.role_id=#{roleUuid}

			and b.RESOURCE_UUID  in

					  #{item}

mapper.select:
SELECT a.ROLE_ID,a.RESOURCE_ID,b.UUID AS OPERATE_UUID,b.FUN_CLASS,b.FUN_METHOD,b.OPERATE_NAME,b.OPERATE_DESC, (CASE WHEN c.OPERATE_UUID IS NOT NULL THEN 'true' ELSE 'false' END) AS CHECKED FROM tab_role_resource a,tab_operate b left join tab_role_operate c ON b.UUID =c.OPERATE_UUID WHERE a.resource_id=b.resource_uuid
AND a.ROLE_ID = #{roleUuid}
AND a.RESOURCE_ID = #{resourceUuid}
order by b.ordernum asc
