mapper.select:
   		select * from (



		 					and b.role_id=#{roleId}






		     	and a.parent_resource_uuid in ( 
			     select t.UUID from(











			 		and a.uuid=#{resourceId}


			 		and a.resource_type=#{resourceType}


			 		and b.role_id=#{roleId}

			 	) t
			 )

			 	union









			 		and a.uuid=#{resourceId}


			 		and a.resource_type=#{resourceType}


			 		and b.role_id=#{roleId}


		) a order by a.ordernum asc

select distinct a.UUID,A.RESOURCE_NAME,A.RESOURCE_TYPE,A.RESOURCE_URL,A.PARENT_RESOURCE_UUID,A.CREATOR_NAME,A.CREATE_TIME,A.REMARK,A.ORDERNUM, (case when c.UUID is not null then 'true' else 'false' end) isParent from tab_resource a left join tab_resource c on a.UUID = c.PARENT_RESOURCE_UUID ,tab_role_resource b where 1=1 and a.UUID = b.RESOURCE_ID
and b.ROLE_ID=#{roleId}
order by a.ordernum asc
