mapper.select:
SELECT ti.*, tpc.PERSON_NAME AS createAccName, tm.create_date, tpm.PERSON_NAME AS modifyAccName, tm.modify_date, tm.audit, tpau.PERSON_NAME AS auditAccName, tm.audit_date AS auditTime, tpr.PERSON_NAME AS releaseAccName, tm.release_date, tm.one_level_classify, tm.release_status, tmpl.publish_expire_date, td.dept_uuid, td.dept_name FROM tab_excellent_innovation ti INNER JOIN tab_module tm ON ti.innovation_uuid = tm.uuid LEFT JOIN tab_person tpc ON tpc.uuid = tm.create_acc_uuid LEFT JOIN tab_person tpm ON tpm.uuid = tm.modify_acc_uuid LEFT JOIN tab_person tpau ON tpau.uuid = tm.audit_acc_uuid LEFT JOIN tab_person tpr ON tpr.uuid = tm.release_acc_uuid LEFT JOIN tab_module_publish tmpl ON tmpl.module_uuid = tm.uuid LEFT JOIN tab_dept td ON ti.dept_uuid = td.dept_uuid
1=1
and ti.innovation_uuid = #{innovationUuid}
and ti.org_name LIKE concat(concat('%',#{orgName}),'%')
and ti.declare_person LIKE concat(concat('%',#{declarePerson}),'%')
and ti.first_complete_person LIKE concat(concat('%',#{firstCompletePerson}),'%')
and td.dept_name LIKE concat(concat('%',#{deptName}),'%')
and ti.other_complete_person LIKE concat(concat('%',#{otherCompletePerson}),'%')
and ti.achievement_name LIKE concat(concat('%',#{achievementName}),'%')
and ti.innovation_date>=#{innovationDateBegin}
and ti.innovation_date<=#{innovationDateEnd}
and tm.audit =#{audit}
and tm.release_status =#{releaseStatus}
and tm.audit_date>=#{auditDateBegin}
and tm.audit_date<=#{auditDateEnd})
and tm.release_date>=#{releaseDateBegin}
and tm.release_date<=#{releaseDateEnd}
and tmpl.publish_expire_date>=#{publishExpireDateBegin}
and tmpl.publish_expire_date<=#{publishExpireDateEnd}
and ti.dept_uuid =#{deptUuid}
and td.groupid like concat(concat('%',#{groupid}),'%')
and (ti.first_complete_person LIKE concat(concat('%',#{queryParameter}),'%') or ti.achievement_name LIKE concat(concat('%',#{queryParameter}),'%') or ti.achievement_content LIKE concat(concat('%',#{queryParameter}),'%') or ti.declare_person LIKE concat(concat('%',#{queryParameter}),'%') or ti.org_name LIKE concat(concat('%',#{queryParameter}),'%') or td.dept_name LIKE concat(concat('%',#{queryParameter}),'%') or ti.other_complete_person LIKE concat(concat('%',#{queryParameter}),'%') )
order by ti.innovation_date desc

