mapper.select:
   		SELECT 
	   		uuid, 
	   		create_acc_uuid, 
	   		create_date, 
	   		modify_acc_uuid, 
	   		modify_date, 
	   		audit, 
	   		audit_acc_uuid, 
	   		audit_date, 
	   		release_acc_uuid, 
	   		release_date, 
	   		one_level_classify, 
	   		two_level_classify 
   		FROM tab_model


   					and create_acc_uuid in(
   					  SELECT tab_person.uuid FROM tab_person WHERE tab_person.person_name LIKE  concat(concat('%',#{createAccName}),'%');
			          )


        		    and create_date>=UNIX_TIMESTAMP(#{createDateBegin})   


      		      and create_date<=UNIX_TIMESTAMP(#{createDateEnd})   


   					and modify_acc_uuid in(
   					  SELECT tab_person.uuid FROM tab_person WHERE tab_person.person_name LIKE  concat(concat('%',#{modifyAccName}),'%');
			          )


        		    and modify_date>=UNIX_TIMESTAMP(#{modifyDateBegin})   


      		      and modify_date<=UNIX_TIMESTAMP(#{modifyDateEnd})   


      		      and  audit =#{audit}


   					and audit_acc_uuid in(
   					  SELECT tab_person.uuid FROM tab_person WHERE tab_person.person_name LIKE  concat(concat('%',#{auditAccName}),'%');
			          )


        		    and audit_date>=UNIX_TIMESTAMP(#{auditDateBegin})  


      		      and audit_date<=UNIX_TIMESTAMP(#{auditDateEnd})   


   					and release_acc_uuid in(
   					  SELECT tab_person.uuid FROM tab_person WHERE tab_person.person_name LIKE  concat(concat('%',#{releaseAccName}),'%');
			          )


        		    and release_date>=UNIX_TIMESTAMP(#{releaseDateBegin})   


      		      and release_date<=UNIX_TIMESTAMP(#{releaseDateEnd})   


      		      and  one_level_classify =#{oneLevelClassify}


      		      and  two_level_classify =#{towLevelClassify}


      		      and  uuid =#{modelUuid}
