mapper.select:
SELECT tn.uuid, tn.news_title, tn.news_subtitle, tn.news_content, tn.news_date, tn.news_author, tn.thumbnail_image_url, tn.news_abstract , tpc.PERSON_NAME as createAccName, tm.create_date, tpm.PERSON_NAME as modifyccName, tm.modify_date, tm.audit, tpau.PERSON_NAME as auditAccName, tm.audit_date, tpr.PERSON_NAME as releaseAccName, tm.release_date, tm.one_level_classify, tm.two_level_classify , tm.release_status, tmpl.publish_expire_date FROM tab_news tn LEFT JOIN tab_module tm ON tm.uuid = tn.uuid LEFT JOIN tab_person tpc ON tpc.uuid = tm.create_acc_uuid LEFT JOIN tab_person tpm ON tpm.uuid = tm.modify_acc_uuid LEFT JOIN tab_person tpau ON tpau.uuid = tm.audit_acc_uuid LEFT JOIN tab_person tpr ON tpr.uuid = tm.release_acc_uuid LEFT JOIN tab_module_publish tmpl ON tmpl.module_uuid = tm.uuid
and tn.news_title LIKE concat(concat('%',#{newsTitle}),'%')
and tn.news_subtitle LIKE concat(concat('%',#{newSubTitle}),'%')
and tn.news_date>=#{newsDateBegin}
and tn.news_date<=#{newsDateEnd}
and tn.news_author LIKE concat(concat('%',#{newAutor}),'%')
and tn.news_abstract LIKE concat(concat('%',#{newsAbstarct}),'%')
and tm.create_acc_uuid in( SELECT tab_person.uuid FROM tab_person WHERE tab_person.person_name LIKE concat(concat('%',#{createAccName}),'%') )
and tm.create_date>=#{createDateBegin}
and tm.create_date<=#{createDateEnd}
and tm.modify_acc_uuid in( SELECT tab_person.uuid FROM tab_person WHERE tab_person.person_name LIKE concat(concat('%',#{modifyAccName}),'%') )
and tm.modify_date>=#{modifyDateBegin}
and tm.modify_date<=#{modifyDateEnd}
and tm.audit =#{audit}
and tm.audit_acc_uuid in( SELECT tab_person.uuid FROM tab_person WHERE tab_person.person_name LIKE concat(concat('%',#{auditAccName}),'%') )
and tm.audit_date>=#{auditDateBegin}
and tm.audit_date<=#{auditDateEnd})
and tm.release_acc_uuid in( SELECT tab_person.uuid FROM tab_person WHERE tab_person.person_name LIKE concat(concat('%',#{releaseAccName}),'%') )
and tm.release_date>=#{releaseDateBegin}
and tm.release_date<=#{releaseDateEnd}
and tm.one_level_classify =#{oneLevelClassify}
and tm.two_level_classify =#{towLevelClassify}
and tm.release_status =#{releaseStatus}
and tmpl.publish_expire_date>=#{publishExpireDateBegin}
and tmpl.publish_expire_date<=#{publishExpireDateEnd}
and tm.create_acc_uuid =#{userId}

SELECT tn.uuid, tn.news_title, tn.news_subtitle, tn.thumbnail_image_url, tn.news_abstract FROM tab_news tn LEFT JOIN tab_module tm ON tm.uuid = tn.uuid LEFT JOIN tab_module_publish tmpl ON tmpl.module_uuid = tm.uuid
and tm.two_level_classify = #{towLevelClassify}
and tmpl.publish_expire_date>=#{publishExpireDate}
and tm.release_status =1
