mapper.select:
SELECT t1.*, ( case when t3.subject_type = '6' then CONCAT(t1.result_value ,'æ˜Ÿ') else t1.result_value end) as res, t3.subject_name, t3.subject_type FROM tab_subject_result t1 INNER JOIN tab_evaluate_info t2 ON t1.evaluate_info_uuid = t2.evaluate_info_uuid LEFT JOIN tab_subject_config t3 ON t1.subject_uuid = t3.subject_uuid AND t2.questionnaire_uuid = t3.questionnaire_uuid
and t2.evaluate_info_uuid = #{evaluateInfoUuid}
and t2.questionnaire_uuid = #{questionnaireUuid}
order by t3.subject_order

mapper.update:
update tab_subject_result
set subject_uuid = #{subjectUuid,jdbcType=VARCHAR},
  evaluate_info_uuid = #{evaluateInfoUuid,jdbcType=VARCHAR},
  result_value = #{resultValue,jdbcType=VARCHAR}
where result_uuid = #{resultUuid,jdbcType=VARCHAR}
