mapper.delete:
    delete from tab_report
    where REPORT_UUID in

				  #{item}

mapper.update:
    update tab_report
    set

         REPORT_TITLE = #{reportTitle,jdbcType=VARCHAR},


        REPORT_USER_NAME = #{reportUserName,jdbcType=VARCHAR},


        REPORT_CONTENT = #{reportContent,jdbcType=VARCHAR},


        REPORT_USER_UUID = #{reportUserUuid,jdbcType=VARCHAR},


	   REPORT_TIME = #{reportTime,jdbcType=TIMESTAMP},


        REPORT_TYPE = #{reportType,jdbcType=VARCHAR},


        LAST_MODIFY_TIME = #{lastModifyTime,jdbcType=TIMESTAMP},


        REPORT_UPDATE_UUID = #{reportUpdateUuid,jdbcType=VARCHAR}


          REPORT_STAUS = #{reportStaus,jdbcType=VARCHAR}


          REPORT_TEL = #{reportTel,jdbcType=VARCHAR}

       where REPORT_UUID = #{reportUuid,jdbcType=VARCHAR}

mapper.select:
select REPORT_UUID as reportUuid, REPORT_TITLE as reportTitle, REPORT_CONTENT as reportContent, REPORT_USER_NAME as reportUserName, REPORT_USER_UUID as reportUserUuid, REPORT_TIME as reportTime, REPORT_TYPE as reportType, REPORT_STAUS as reportStaus, LAST_MODIFY_TIME as lastModifyTime, REPORT_UPDATE_UUID as reportUpdateUuid, REPORT_TEL as reportTel, IMAGE_PATH as imagePath from tab_report
1=1
and REPORT_UUID = #{reportUuid}
and REPORT_TITLE LIKE concat(concat('%',#{reportTitle}),'%')
and REPORT_USER_NAME LIKE concat(concat('%',#{reportUserName}),'%')
and REPORT_CONTENT LIKE concat(concat('%',#{reportContent}),'%')
and REPORT_UUID = #{reportUserUuid}
and REPORT_TIME>=#{reportTimeBegin}
and REPORT_TIME<=#{reportTimeEnd})
and REPORT_TYPE = #{reportType}
and REPORT_STAUS = #{reportStaus}
and REPORT_TEL LIKE concat(concat('%',#{reportTel}),'%')
and ( REPORT_TITLE LIKE concat(concat('%',#{queryParameter}),'%') or REPORT_USER_NAME LIKE concat(concat('%',#{queryParameter}),'%') or REPORT_CONTENT LIKE concat(concat('%',#{queryParameter}),'%') or REPORT_TEL LIKE concat(concat('%',#{queryParameter}),'%') or REPORT_TYPE = #{queryParameter} )
