mapper.select:
select t1.topic_uuid,t1.topic_name,t1.topic_content,t1.topic_status,t1.block_uuid,t1.create_person_uuid, t1.telephone,t1.last_comment_person_uuid,t1.last_comment_uuid,t1.create_person_name,t1.image_path, t1.release_status,t1.release_person_uuid, date_format(t1.release_time, '%Y-%m-%d %H:%i:%S') AS release_time, date_format(t1.create_date, '%Y-%m-%d %H:%i:%S') AS create_date, date_format(t1.last_comment_date, '%Y-%m-%d %H:%i:%S') AS last_comment_date, t2.nums, t3.person_name as lastCommentPerson, t4.person_name as releasePersonName, t5.laudNums from tab_topics t1 left join tab_page_view t2 on t1.topic_uuid = t2.uuid left join tab_person t3 on t1.last_comment_person_uuid = t3.uuid left join tab_person t4 on t1.release_person_uuid = t4.uuid left join ( SELECT module_uuid, COUNT(*) AS laudNums FROM tab_laud GROUP BY module_uuid ) t5 on t5.module_uuid = t1.topic_uuid
1 = 1 and t1.topic_status != 3
and t1.topic_uuid = #{topicUuid}
and t1.block_uuid = #{blockUuid}
and t1.topic_status = #{topicStatus}
and t1.topic_name like concat(concat('%',#{topicName}),'%')
and t1.topic_content like concat(concat('%',#{topicContent}),'%')
and t1.create_person_name like concat(concat('%',#{createPersonName}),'%')
and t1.telephone like concat(concat('%',#{telephone}),'%')
and t3.person_name like concat(concat('%',#{lastCommentPerson}),'%')
and t1.create_date >=#{createDateBegin}
and t1.create_date <=#{createDateEnd}
and t1.last_comment_date >=#{lastCommentDateBegin}
and t1.last_comment_date <=#{lastCommentDateEnd}
and t1.release_status = #{releaseStatus}
and t1.release_status = 1 or (t1.release_status = 2 and t1.create_person_uuid = #{personUuid})
and (t1.topic_name like concat(concat('%',#{queryParameter}),'%') or t1.topic_content LIKE concat(concat('%',#{queryParameter}),'%') or t1.create_person_name LIKE concat(concat('%',#{queryParameter}),'%') or t1.telephone like concat(concat('%',#{queryParameter}),'%') )
order by create_date desc

mapper.update:
  	update tab_topics set release_status = #{releaseStatus},release_time = #{releaseTime},
  		release_person_uuid = #{releasePersonUuid}
  	where topic_uuid in

			#{item}
